mapper.select:
SELECT COUNT(*)
FROM word
WHERE word LIKE CONCAT(#{word}, '%');

SELECT g.glossary_id, g.list_name, g.description, g.is_public, g.created_at, g.category_id, g.cover_url, g.cover_meta FROM glossary g WHERE is_public = 1
AND g.list_name LIKE CONCAT('%', #{searchWord}, '%')
AND g.category_id = #{categoryId}
LIMIT #{current}, #{size}
